arrayPipelines.data:
[
  {
    $match: {
       _id: {{searchPostcard.value==='' ? { $exists: true } : searchPostcard.value }},
      organization: {{Organization_Select.selectedOptionValue==='all' ?  { $exists: true } : Organization_Select.selectedOptionValue}},
      status: {{Status_Select.selectedOptionValue==='ALL' ?  { $exists: true } : Status_Select.selectedOptionValue}},
      live: {{ Live_Switch.isSwitchedOn }},
      createdAt: {$gte: { $date: {{StartDate_Postcard_Picker.selectedDate }} }, $lte: { $date: {{ EndDate_Postcard_Picker.selectedDate }} }},
    }
  },
  { $sort  : { createdAt : -1} },
  { $skip : {{searchPostcard.value==='' ?  (appsmith.store.postcardsSkipCount || 0) : 0 }} },
  {
    $lookup: {
    	from: 'organizations',
    	localField: 'organization',
    	foreignField: '_id',
    	as: 'org'
  	}
  },
  {
  	$project: {
  		_id: 1,
  		live: 1,
  		org: 1,
  		status: 1,
  		to: 1,
  		from: 1,
  		renderedPDFS3Key: 1,
      uploadedPDFS3Key: 1,
  		createdAt: 1,
  		organization: 1,
  		size: 1
  	}
  },
]

