INSERT INTO "CustomerLineItems" ("Id","InvoiceID","CustomerPriceID","Qty","SubItemID","Destination") 
VALUES 
	 {{appsmith.store.newList.map((d) => { 
		return"(" + "'customer_item_" + d.id+ "'," + "(SELECT i.\"InvoiceID\" FROM \"public\".\"Invoice\" i INNER JOIN \"public\".\"Customers\" c1 ON ( c1.\"Id\" = i.\"CustomerID\")  WHERE i.\"InvoiceDate\" >= '"+ moment(DatePicker1.selectedDate).format("YYYY-MM-DD") +"' AND	c1.\"OrgID\" = '" + d.orgID + "' ),(SELECT c1.\"Id\"FROM \"public\".\"CustomerPrice\" c1  	INNER JOIN \"public\".\"Products\" p ON ( p.\"Id\" = c1.\"ProductID\"  )   	INNER JOIN \"public\".\"Customers\" c2 ON ( c2.\"Id\" = c1.\"CustomerID\"  )  WHERE p.\"ProductDescription\" = " + "'" + d.productDesc + "' AND 	c2.\"OrgID\" ='" + d.orgID + "'),'" + d.quantity + "',"  + (d.parentID === null ? null : `'customer_item_${d.parentID}'`) + ",'" + d.destinationCountryCode + "')"}).join(",")  }};
